import "./global.css";
import s from "./style.module.css";
import { TVShowAPI } from "./api/tv-show";
import { useEffect, useState } from "react";
import { BACKDROP_BASE_URL } from "./config";


export function App() {
    const [currentTVShow, setCurrentTVShow] = useState();
    async function fetchPopulars() {
        const populars = await TVShowAPI.fetchPopulars();
        if(populars.length > 0) {
            setCurrentTVShow(populars[0]);
        }
    }

    useEffect(()=>{
        fetchPopulars();
    }, [])

    console.log("***", currentTVShow);
    return ( 
    <div className={s.main_container}
    style={{background: `linear-gradient(rgba(0,0,0,0.55), rgba(0,0,0,0.55)), url("${BACKDROP_BASE_URL}${currentTVShow.backdrop_path}") no-repeat center / cover`}}>
        <div className={s.header}>
            <div className="row">
                <div className="col-4">
                    <div>Logo</div>
                    <div>Subtitile</div>
                </div>
                <div className="col-md-12 col-lg-4"><input type="text"/></div>
            </div>
        </div>
        <div className={s.tv_show_detail}>Details</div>
        <div className={s.recommandations}>Recommandations</div>
    </div>
    );
}
